import { Loader } from "@googlemaps/js-api-loader";
import { useEffect, useRef, useState } from "react";
import { connect } from "react-redux";

import { axi } from "../../utils/config";
import { addFoodCircle, addWaterCircle } from "./circle";
import addMarker from "./marker";
import icongray from "../../images/button-gray.png";
import styles from "./home.module.css";

function Map(props) {
	const [loading, setLoading] = useState(false);
	const mapElement = useRef(null);
	const centerElement = useRef(null);
	const map = useRef({});
	const markerRef = useRef(null);
	// const defaultStyle = { width: "100vw", height: "100vh" };

	useEffect(
		() => {
			const loader = new Loader({
				apiKey: props.apiKey || "AIzaSyDit4G0bMDP54KRwVqRTzvFtFGMNPRt_mo",
				version: "weekly",
				libraries: ["places"],
			});

			if (!props.initLocation || props.initLocation.lat === 0) return;

			loader
				.load()
				.then(() => {
					map.current = new window.google.maps.Map(mapElement.current, {
						zoom: 16,
						center: props.initLocation,
						scaleControl: false,
						streetViewControl: false,
						rotateControl: false,
						mapTypeControl: false,
					});
					map.current.controls[
						window.google.maps.ControlPosition.RIGHT_BOTTOM
					].push(centerElement.current);
					addMarker(markerRef, props.initLocation, map.current);
					addFoodCircle(map.current, props.cityList, props.user);
					addWaterCircle(map.current, props.cityList, props.user);
					setLoading(true);
				})
				.catch(console.log)
				.then(() => {
					axi("get", "/food")
						.then((res) => {
							props.setCityList({
								...props.cityList,
								food: [...props.cityList.food, ...res.response],
							});
						})
						.catch(console.log);
					axi("get", "/water")
						.then((res) => {
							props.setCityList({
								...props.cityList,
								water: [...props.cityList.water, ...res.response],
							});
						})
						.catch(console.log);
				})
				.catch(console.log);
		},
		//eslint-disable-next-line
		[props.initLocation]
	);

	useEffect(
		() => {
			console.log("cityList", props.cityList);
			if (map.current.setCenter) {
				addFoodCircle(map.current, props.cityList, props.user);
				addWaterCircle(map.current, props.cityList, props.user);
			}
		},
		//eslint-disable-next-line
		[props.cityList]
	);

	useEffect(() => {
		if (markerRef.current) {
			markerRef.current.setMap(null);
			addMarker(markerRef, props.locationState, map.current);
		}
	}, [props.locationState]);
	return (
		<div className={styles.map} ref={mapElement}>
			<div
				ref={centerElement}
				style={{
					backgroundColor: "#fff",
					border: "2px solid #fff",
					borderRadius: "3px",
					boxShadow: "0 2px 6px rgba(0,0,0,.3)",
					cursor: "pointer",
					marginBottom: "22px",
					textAlign: "center",
					marginRight: "10px",
					top: "null",
					display: loading ? "block" : "none",
				}}
				onClick={() => {
					map.current.setCenter(props.locationState);
				}}
			>
				<div
					style={{
						color: "rgb(25,25,25)",
						fontFamily: "Roboto,Arial,sans-serif",
						fontSize: "24px",
						lineHeight: "36px",
						paddingLeft: "9px",
						paddingRight: "9px",
					}}
				>
					<img src={icongray} alt="" />
				</div>
			</div>
		</div>
	);
}

const mapStateToProps = (state) => {
	return {
		user: state.user,
	};
};

export default connect(mapStateToProps)(Map);
