import { Loader } from "@googlemaps/js-api-loader";
import { useEffect, useRef, useState } from "react";
import { connect } from "react-redux";

import { axi } from "../../../utils/config";
import { addFoodCircle, addWaterCircle } from "./circle";
import Spinner from "../../../common/Spinner";
import addMarker from "./marker";
import icongray from "../../../images/button-gray.png";
import styles from "./map.module.css";

function Mark() {
	return (
		<div
			style={{
				color: "rgb(25,25,25)",
				fontFamily: "Roboto,Arial,sans-serif",
				fontSize: "24px",
				lineHeight: "36px",
				paddingLeft: "9px",
				paddingRight: "9px",
			}}
		>
			<img src={icongray} alt="" />
		</div>
	);
}

function CenterButton(props) {
	return (
		<div
			ref={props.centerElement}
			style={{
				backgroundColor: "#fff",
				border: "2px solid #fff",
				borderRadius: "3px",
				boxShadow: "0 2px 6px rgba(0,0,0,.3)",
				cursor: "pointer",
				marginBottom: "22px",
				textAlign: "center",
				marginRight: "10px",
				top: "null",
				display: loaded ? "block" : "none",
			}}
			onClick={() => {
				props.map.current.setCenter(props.locationState);
			}}
		>
			<Mark />
		</div>
	);
}

function Map(props) {
	const [loaded, setLoaded] = useState(false);
	const mapElement = useRef(null);
	const centerElement = useRef(null);
	const map = useRef({});
	const markerRef = useRef(null);

	useEffect(
		() => {
			const loader = new Loader({
				apiKey: props.apiKey || "AIzaSyDit4G0bMDP54KRwVqRTzvFtFGMNPRt_mo",
				version: "weekly",
				libraries: ["places"],
			});

			if (!props.initLocation || props.initLocation.lat === 0) return;

			loader
				.load()
				.then(() => {
					map.current = new window.google.maps.Map(mapElement.current, {
						zoom: 16,
						center: props.initLocation,
						scaleControl: false,
						streetViewControl: false,
						rotateControl: false,
						mapTypeControl: false,
					});
					map.current.controls[
						window.google.maps.ControlPosition.RIGHT_BOTTOM
					].push(centerElement.current);
					addMarker(markerRef, props.initLocation, map.current);
					addFoodCircle(
						map.current,
						props.foodListOfCities,
						props.user,
						props.setFoodListOfCities
					);
					addWaterCircle(
						map.current,
						props.waterListOfCities,
						props.user,
						props.setWaterListOfCities
					);
					setLoaded(true);
				})
				.catch(console.log)
				.then(() => {
					axi("get", "/food")
						.then((res) => {
							props.setFoodListOfCities(res.response);
						})
						.catch(console.log);
					axi("get", "/water")
						.then((res) => {
							props.setWaterListOfCities(res.response);
						})
						.catch(console.log);
				})
				.catch(console.log);
			// .catch(console.log);
		},
		//eslint-disable-next-line
		[props.initLocation]
	);

	useEffect(
		() => {
			if (map.current.setCenter) {
				addFoodCircle(
					map.current,
					props.foodListOfCities,
					props.user,
					props.setFoodListOfCities
				);
			}
		},
		//eslint-disable-next-line
		[props.foodListOfCities]
	);
	useEffect(
		() => {
			if (map.current.setCenter) {
				addWaterCircle(
					map.current,
					props.waterListOfCities,
					props.user,
					props.setWaterListOfCities
				);
			}
		},
		//eslint-disable-next-line
		[props.waterListOfCities]
	);

	useEffect(() => {
		if (markerRef.current) {
			markerRef.current.setMap(null);
			addMarker(markerRef, props.locationState, map.current);
		}
	}, [props.locationState]);

	return (
		<>
			<div
				className={styles.map}
				style={loaded ? {} : { display: "none" }}
				ref={mapElement}
			>
				<CenterButton
					centerElement={centerElement}
					map={map}
					locationState={prop.locationState}
				/>
			</div>
			<div
				style={
					loaded
						? { display: "none" }
						: {
								display: "flex",
								alignItems: "center",
								justifyContent: "center",
								height: "100%",
						  }
				}
			>
				<Spinner />
			</div>
		</>
	);
}

const mapStateToProps = (state) => {
	return {
		user: state.user,
	};
};

export default connect(mapStateToProps)(Map);
