import { useState, useEffect, useRef } from "react";
import { connect } from "react-redux";

import Map from "./map/Map";
import { axi } from "../../utils/config";
import Header from "../../common/Header";
import styles from "./home.module.css";
import food from "../../images/food-bowl.svg";
import water from "../../images/water-bowl.svg";
import submitFood from "./submitFood";

function HomePage(props) {
	const [foodListOfCities, setFoodListOfCities] = useState([]);
	const [waterListOfCities, setWaterListOfCities] = useState([]);
	const [initLocation, setInitLocation] = useState({ lat: 0, lng: 0 });
	const [locationState, setLocationState] = useState({ lat: 0, lng: 0 });
	const [foodTime, setFoodTime] = useState(false);
	const [waterTime, setWaterTime] = useState(false);
	const ref = useRef("");
	const id = useRef(0);
	useEffect(() => {
		if (
			navigator.userAgent.toLowerCase().includes("android") &&
			navigator.userAgent.toLowerCase().includes("firefox")
		)
			alert(
				"firefox mobile browser has a geolocation service issue, please use other browsers to avoid location issues"
			);
	}, []);

	useEffect(
		() => {
			navigator.geolocation.getCurrentPosition(
				(l) => {
					setInitLocation({ lat: l.coords.latitude, lng: l.coords.longitude });
					setLocationState({ lat: l.coords.latitude, lng: l.coords.longitude });
				},
				(err) => console.log("getCurrentPosition error occour ", err),
				{ timeout: 15000 }
			);
			id.current = navigator.geolocation.watchPosition(
				(l) => {
					setLocationState({
						lat: l.coords.latitude,
						lng: l.coords.longitude,
					});
				},
				(err) => console.log("watchPosition error occour ", err),
				{ timeout: 15000 }
			);
			return () => {
				navigator.geolocation.clearWatch(id.current);
			};
		},
		//eslint-disable-next-line
		[]
	);

	const submitFoodArgs = {
		locationState,
		setFoodTime,
		ref,
		setFoodListOfCities,
		foodListOfCities,
	};

	function submitWater() {
		setTimeout(() => {
			setWaterTime(false);
		}, 15000);
		const arg = {
			id: Date.now(),
			lat: locationState.lat,
			lng: locationState.lng,
			info: ref.current.value,
			token: localStorage.getItem("token"),
		};
		if (waterTime) return alert("You cannot send request immediately");
		setWaterTime(true);
		return axi("post", "/water", arg)
			.then((res) => {
				if (res.status === "accepted") {
					ref.current.value = "";
					return setWaterListOfCities([...waterListOfCities, res.response]);
				}
				alert(res.info);
			})
			.catch((err) => console.log("submit error", err));
	}

	return (
		<div className={styles.container}>
			<div className={styles.header}>
				<Header />
			</div>
			<div className={styles.mapGroup}>
				<div className={styles.mapContainer}>
					<Map
						initLocation={initLocation}
						foodListOfCities={foodListOfCities}
						waterListOfCities={waterListOfCities}
						apiKey={process.env.REACT_APP_APIKEY}
						setFoodListOfCities={setFoodListOfCities}
						setWaterListOfCities={setWaterListOfCities}
						locationState={locationState}
					/>
				</div>
				{props.isLogin && (
					<div className={styles.form}>
						<p>
							“The way you treat the animals around you will be the nature of
							the animal within you”
						</p>
						<p className={styles.formP}> ― Sir P.S. Jagadeesh Kumar</p>
						<div className={styles.formGroup}>
							<div className={styles.inputGroup}>
								<input
									type="text"
									id="info"
									placeholder=" info (optional)"
									ref={ref}
								/>
							</div>
							<div className={styles.buttonGroup}>
								<button onClick={() => submitFood(submitFoodArgs)}>
									<img src={food} alt="" />
									food
								</button>
								<button onClick={submitWater}>
									<img src={water} alt="" />
									water
								</button>
							</div>
						</div>
					</div>
				)}
			</div>
		</div>
	);
}

const mapStateToProps = (state) => {
	return {
		isLogin: state.isLogin,
	};
};

export default connect(mapStateToProps)(HomePage);
